mapper.select:
SELECT COUNT(user) AS pv, COUNT(DISTINCT user) AS uv, COUNT(DISTINCT ip) AS uip FROM t_link_access_logs WHERE gid IN
#{gid}
AND create_time BETWEEN #{param.startDate} and #{param.endDate} GROUP BY gid;

SELECT ip, COUNT(ip) AS `count` FROM t_link_access_logs WHERE gid IN
#{gid}
AND create_time BETWEEN #{param.startDate} and #{param.endDate} GROUP BY ip ORDER BY `count` DESC LIMIT 5;

SELECT SUM(old_user) AS oldUserCnt, SUM(new_user) AS newUserCnt FROM ( SELECT CASE WHEN COUNT(DISTINCT DATE(create_time)) > 1 THEN 1 ELSE 0 END AS old_user, CASE WHEN COUNT(DISTINCT DATE(create_time)) = 1 AND MAX(create_time) >= #{param.startDate} AND MAX(create_time) = #{param.endDate} THEN 1 ELSE 0 END AS new_user FROM t_link_access_logs WHERE gid IN
#{gid}
GROUP BY user ) AS user_counts;
